// Custom Service Worker for Push Notifications
// This file will be imported by the main SW generated by next-pwa

// Handle push events from the server
self.addEventListener('push', (event) => {
    console.log('[SW Push] Push event received');

    let data = {
        title: '¡Nuevo Yo-kai disponible!',
        body: '¡Un nuevo desafío diario te espera! ¿Podrás adivinar el Yo-kai de hoy?',
        icon: '/icons/icon-192.png',
        badge: '/icons/icon-192.png',
        tag: 'daily-yokai',
        url: '/'
    };

    // Try to parse push data if available
    if (event.data) {
        try {
            const pushData = event.data.json();
            data = { ...data, ...pushData };
        } catch (e) {
            console.log('[SW Push] Could not parse push data as JSON, using text');
            data.body = event.data.text() || data.body;
        }
    }

    const options = {
        body: data.body,
        icon: data.icon || '/icons/icon-192.png',
        badge: data.badge || '/icons/icon-192.png',
        tag: data.tag || 'yokaidle-notification',
        requireInteraction: false,
        vibrate: [200, 100, 200],
        data: {
            url: data.url || '/',
            timestamp: Date.now(),
            type: data.type || 'daily-yokai'
        },
        actions: [
            {
                action: 'play',
                title: '¡Jugar ahora!'
            },
            {
                action: 'dismiss',
                title: 'Más tarde'
            }
        ]
    };

    event.waitUntil(
        self.registration.showNotification(data.title, options)
    );
});

// Handle notification click
self.addEventListener('notificationclick', (event) => {
    console.log('[SW Push] Notification click received');

    event.notification.close();

    // Handle different actions
    if (event.action === 'dismiss') {
        return;
    }

    // Default action: open the app
    const targetUrl = event.notification.data?.url || '/';

    event.waitUntil(
        clients.matchAll({ type: 'window', includeUncontrolled: true })
            .then((clientList) => {
                // Check if the app is already open
                for (const client of clientList) {
                    if (client.url.includes(self.registration.scope) && 'focus' in client) {
                        client.navigate(targetUrl);
                        return client.focus();
                    }
                }
                // If not open, open a new window
                if (clients.openWindow) {
                    return clients.openWindow(targetUrl);
                }
            })
    );
});

// Handle notification close
self.addEventListener('notificationclose', (event) => {
    console.log('[SW Push] Notification closed by user');
});

// Handle messages from the main app
self.addEventListener('message', (event) => {
    if (event.data && event.data.type === 'SKIP_WAITING') {
        self.skipWaiting();
    }

    // Handle subscription refresh request
    if (event.data && event.data.type === 'CHECK_PUSH_SUBSCRIPTION') {
        self.registration.pushManager.getSubscription()
            .then((subscription) => {
                event.ports[0].postMessage({
                    type: 'PUSH_SUBSCRIPTION_STATUS',
                    isSubscribed: !!subscription,
                    subscription: subscription
                });
            });
    }
});

console.log('[SW Push] Push notification handlers loaded');
